import sStore from '../Store/index.js';
import { isArray, initArray, fill } from '../Array/index.js';
import { isTrueValue } from '../Object/index.js';
import { padStart, padEnd } from '../String/index.js';
import { pad0 } from '../Number/index.js';
import { stopPropagation, preventDefault, stopAndPrevent } from '../Dom/index.js';
import { noop, deepClone } from '../Function/index.js';
import { getUrlParams, setUrlParams } from '../Url/index.js';
import '../RegExp/index.js';
import '../RegExp/password.js';
import '../RegExp/phone.js';

/**
 * @since 0.0.1
 * @description 包使用默认引入，起名规则为s${大驼峰子模块名}
 */
/**
 * @module sCommon
 * @since 0.0.1
 * @description sCommon模块, 整合高频通用工具函数
 * @see {@link sStore}
 * @see {@link sArray.isArray}
 * @see {@link sArray.initArray}
 * @see {@link sArray.fill}
 * @see {@link sObject.isTrueValue}
 * @example
 * // first import sCommon
 * import { sCommon } from 'js-simple-utils'
 * // then use sCommon
 * sCommon.isArray('123@gmail.com') // false
 * sCommon.isTrueValue('') // false
 */
/**
 *@ignore
 */
const sCommon = Object.assign(Object.assign({}, sStore), { 
    // from sArray
    isArray,
    initArray,
    fill,
    // from sObject
    isTrueValue,
    // from sString
    padStart,
    padEnd,
    // from sNumber
    pad0,
    // from sDom
    stopPropagation,
    preventDefault,
    stopAndPrevent,
    // from sFunction
    noop,
    deepClone,
    // from sUrl
    getUrlParams,
    setUrlParams });

export { sCommon as default };
